import React from 'react';
import { useDispatch } from 'react-redux';
import { updateCartItem, removeFromCart } from '../../store/slices/cartSlice';

const CartItem = ({ item }) => {
  const dispatch = useDispatch();

  const handleQuantityChange = (newQuantity) => {
    if (newQuantity > 0) {
      dispatch(updateCartItem({ itemId: item.id, quantity: newQuantity }));
    }
  };

  const handleRemove = () => {
    dispatch(removeFromCart(item.id));
  };

  return (
    <div className="flex items-center py-4 border-b">
      <img
        src={item.image_url || '/api/placeholder/80/80'}
        alt={item.product_name}
        className="h-20 w-20 object-cover rounded"
      />
      <div className="flex-1 ml-4">
        <h3 className="text-lg font-medium">{item.product_name}</h3>
        <p className="text-gray-600">${item.price}</p>
      </div>
      <div className="flex items-center">
        <button
          onClick={() => handleQuantityChange(item.quantity - 1)}
          className="px-2 py-1 border rounded-l"
        >
          -
        </button>
        <span className="px-4 py-1 border-t border-b">
          {item.quantity}
        </span>
        <button
          onClick={() => handleQuantityChange(item.quantity + 1)}
          className="px-2 py-1 border rounded-r"
        >
          +
        </button>
      </div>
      <div className="ml-8">
        <p className="text-lg font-medium">${(item.price * item.quantity).toFixed(2)}</p>
      </div>
      <button
        onClick={handleRemove}
        className="ml-4 text-red-500 hover:text-red-700"
      >
        Remove
      </button>
    </div>
  );
};

export default CartItem;
