import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateQuantity, removeFromCart } from "../store/slices/cartSlice";
import { useNavigate } from "react-router-dom";
import { Button } from "../components/ui/button";

const CartPage = () => {
  const cartItems = useSelector((state) => state.cart.cartItems);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [cartData, setCartData] = useState(cartItems);

  // Ensure component updates when cartItems change
  useEffect(() => {
    setCartData(cartItems);
  }, [cartItems]);

  const handleQuantityChange = (id, quantity) => {
    if (quantity > 0) {
      dispatch(updateQuantity({ id, quantity }));
    }
  };

  const handleRemoveItem = (id) => {
    dispatch(removeFromCart(id));
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Shopping Cart</h1>

      {cartData.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div className="space-y-4">
          {cartData.map((item) => (
            <div key={item.id} className="flex items-center justify-between p-4 border rounded">
              <img src={item.image_url} alt={item.name} className="w-20 h-20 object-cover rounded" />
              <div className="flex-1 ml-4">
                <h2 className="text-lg font-bold">{item.name}</h2>
                <p className="text-gray-600">${item.price.toLocaleString()}</p>
              </div>
              <input
                type="number"
                value={item.quantity}
                onChange={(e) => handleQuantityChange(item.id, Number(e.target.value))}
                className="w-16 text-center border p-1"
              />
              <Button className="bg-red-500 text-white px-4" onClick={() => handleRemoveItem(item.id)}>
                Remove
              </Button>
            </div>
          ))}
        </div>
      )}

      <Button className="mt-6 w-full" onClick={() => navigate("/")}>
        Continue Shopping
      </Button>
    </div>
  );
};

export default CartPage;
