import { useSelector, useDispatch } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import { logout } from "../../store/slices/authSlice";

const Header = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user, isAuthenticated } = useSelector((state) => state.auth);

  const handleLogout = () => {
    dispatch(logout()); // Dispatch logout action
    navigate("/login", { replace: true }); // Redirect to login page
  };

  return (
    <header className="bg-gray-900 text-white p-4 flex justify-between items-center">
      {/* Left Side: Electronics */}
      <h1 className="text-xl font-bold">Tech </h1>

      {/* Right Side: Auth & Cart */}
      <div className="flex items-center space-x-4">
        {isAuthenticated ? (
          <>
            <span className="text-sm font-semibold">
              {user ? `Hello, ${user.name || user.username}` : "User"}
            </span>
            <button
              onClick={handleLogout}
              className="bg-red-500 hover:bg-red-600 px-4 py-2 rounded"
            >
              Logout
            </button>
            {!user?.is_admin && (
              <Link to="/cart" className="bg-blue-500 hover:bg-blue-600 px-4 py-2 rounded">
                Cart
              </Link>
            )}
          </>
        ) : (
          <Link to="/login" className="bg-green-500 hover:bg-green-600 px-4 py-2 rounded">
            Login
          </Link>
        )}
      </div>
    </header>
  );
};

export default Header;
