import { useSelector } from 'react-redux';

export const useAuth = () => {
  const { user, isAuthenticated, loading } = useSelector(state => state.auth);
  return {
    user,
    isAuthenticated,
    isAdmin: user?.is_admin || false,
    loading
  };
};
