import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addToCart } from "../store/slices/cartSlice";
import { useNavigate } from "react-router-dom";
import { Card, CardContent } from "../components/ui/card";
import { Button } from "../components/ui/button";
import api from "../services/api";
import Header from "../components/layout/Header";

const Dashboard = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { isAuthenticated } = useSelector((state) => state.auth);
  const cartItems = useSelector((state) => state.cart.cartItems); // Get cart state

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await api.get("/products");
        setProducts(response.data.items || []);
      } catch (err) {
        setError("Failed to fetch products");
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  const handleAddToCart = (product) => {
    console.log("Attempting to add product to cart:", product);
    if (!isAuthenticated) {
      navigate("/login");
      return;
    }
    dispatch(addToCart(product));
    alert(`${product.name} added to cart!`); // Show confirmation message
  };

  return (
    <div className="p-6">
      <Header />
      <div className="w-full h-56 bg-blue-500 text-white flex items-center justify-center text-3xl font-bold">
        Welcome to Our Store
      </div>

      {/* Show Cart Items */}
      <div className="mt-6 p-4 border rounded-lg shadow-lg bg-white">
        <h2 className="text-xl font-bold">ðŸ›’ Shopping Cart</h2>
        {cartItems.length === 0 ? (
          <p className="text-gray-600">No items in cart</p>
        ) : (
          <ul>
            {cartItems.map((item) => (
              <li key={item.id} className="mt-2">
                {item.name} - Quantity: {item.quantity}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Show Product Grid */}
      {loading ? (
        <p className="text-center mt-4">Loading products...</p>
      ) : error ? (
        <p className="text-center mt-4 text-red-500">{error}</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 mt-6">
          {products.map((product) => (
            <Card key={product.id} className="p-4">
              <img
                src={product.image_url}
                alt={product.name}
                className="w-full h-40 object-cover rounded"
              />
              <CardContent className="mt-2">
                <h2 className="text-lg font-bold">{product.name}</h2>
                <p className="text-gray-600">${product.price}</p>
                <Button
                  className="mt-2 w-full"
                  onClick={() => handleAddToCart(product)}
                >
                  Add to Cart
                </Button>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
