import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addToCart } from "../store/slices/cartSlice";
import { useNavigate } from "react-router-dom";
import api from "../services/api";
import Header from "../components/layout/Header";
import Footer from "../components/layout/Footer";

// Custom Button Component
const Button = ({ children, onClick, className = "", ...props }) => {
  return (
    <button
      onClick={onClick}
      className={`bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded transition-colors duration-200 ${className}`}
      {...props}
    >
      {children}
    </button>
  );
};

// Custom Card Component
const Card = ({ children, className = "" }) => {
  return (
    <div className={`bg-white shadow-md rounded-lg overflow-hidden ${className}`}>
      {children}
    </div>
  );
};

// Custom CardContent Component
const CardContent = ({ children, className = "" }) => {
  return <div className={`p-4 ${className}`}>{children}</div>;
};

const Dashboard = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { isAuthenticated } = useSelector((state) => state.auth);
  const cartItems = useSelector((state) => state.cart.cartItems);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await api.get("/products");
        setProducts(response.data.items || []);
      } catch (err) {
        setError("Failed to fetch products");
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  const handleAddToCart = (product) => {
    console.log("Attempting to add product to cart:", product);
    if (!isAuthenticated) {
      navigate("/login");
      return;
    }
    dispatch(addToCart(product));
    alert(`${product.name} added to cart!`);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      
      {/* Hero Section */}
      <div className="w-full h-56 bg-gradient-to-r from-blue-600 to-blue-700 text-white flex items-center justify-center">
        <h1 className="text-4xl font-bold">Welcome to Our Store</h1>
      </div>

      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Cart Section */}
        <div className="mb-8 bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-2xl font-bold mb-4 flex items-center">
            <span className="mr-2">ðŸ›’</span> Shopping Cart
          </h2>
          {cartItems.length === 0 ? (
            <p className="text-gray-600">No items in cart</p>
          ) : (
            <ul className="space-y-3">
              {cartItems.map((item) => (
                <li 
                  key={item.id} 
                  className="flex items-center justify-between border-b pb-2"
                >
                  <span className="font-medium">{item.name}</span>
                  <span className="text-gray-600">Quantity: {item.quantity}</span>
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Products Grid */}
        {loading ? (
          <div className="text-center py-12">
            <p className="text-gray-600">Loading products...</p>
          </div>
        ) : error ? (
          <div className="text-center py-12">
            <p className="text-red-500">{error}</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {products.map((product) => (
              <Card key={product.id}>
                <div className="aspect-w-16 aspect-h-12">
                  <img
                    src={product.image_url}
                    alt={product.name}
                    className="w-full h-48 object-cover"
                  />
                </div>
                <CardContent>
                  <h2 className="text-lg font-bold mb-2">{product.name}</h2>
                  <p className="text-gray-600 mb-4">${product.price}</p>
                  <Button
                    onClick={() => handleAddToCart(product)}
                    className="w-full"
                  >
                    Add to Cart
                  </Button>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default Dashboard;
